import ky from 'ky';

// Only 1 ðŸ˜ƒ
const baseUrl = 'http://localhost:8080/records';

const api = {
  index() {
    return ky.get(baseUrl).json();
  },

  update(payload, id) {
    return ky.put(`${baseUrl}/${id}`, { json: payload });
  },

  create(payload) {
    return ky.post(baseUrl, { json: payload }).json();
  },

  delete(id) {
    return ky.delete(`${baseUrl}/${id}`);
  },
};

export default api;
